import { Component } from "react";

export default class Card extends Component {
  constructor(props) {
    super(props);
    this.state = {
      title: props.title,
      image: props.image,
      desc: props.desc,
      link: props.link,
    };
  }
  render() {
    return <div className="bg-blue-200 m-12">

    <h2>{this.state.title}</h2>
    <img className="text-center" src={this.state.image}/>
    <h3>{this.state.desc}</h3>

    </div>;
  }
}
